<span class="token keyword">print</span> <span class="token punctuation">(</span>class2<span class="token punctuation">[</span><span class="token number">1</span><span class="token punctuation">]</span><span class="token punctuation">)</span>

class3<span class="token operator">=</span>favorite_class<span class="token punctuation">[</span><span class="token string">'madongmei'</span><span class="token punctuation">]</span>
<span class="token keyword">print</span> <span class="token punctuation">(</span>class3<span class="token punctuation">)</span>
<span class="token keyword">print</span> <span class="token punctuation">(</span>class3<span class="token punctuation">[</span><span class="token number">1</span><span class="token punctuation">]</span><span class="token punctuation">)</span>

<span class="token keyword">print</span> <span class="token punctuation">(</span>favorite_class <span class="token punctuation">[</span><span class="token string">'lilei'</span><span class="token punctuation">]</span><span class="token punctuation">[</span><span class="token number">1</span><span class="token punctuation">]</span><span class="token punctuation">,</span> favorite_class <span class="token punctuation">[</span><span class="token string">'madongmei'</span><span class="token punctuation">]</span><span class="token punctuation">[</span><span class="token number">1</span><span class="token punctuation">]</span><span class="token punctuation">,</span> sep<span class="token operator">=</span> <span class="token string">','</span><span class="token punctuation">)</span>
